    svg.append('g').attr('class', 'y axis').attr('transform', 'translate(' + (innerWidth + margin.left) + ', ' + margin.top + ')').style("opacity", 0).transition().duration(720).ease(d3.easeQuad).style('opacity', 1).call(d3.axisRight(y).ticks(5).tickSize(0).tickPadding(32));
    svg.append('line').attr('class', 'bottom-axis').attr("x1", 0).attr("y1", height - margin.bottom).attr("x2", width).attr("y2", height - margin.bottom).style("opacity", 0).transition().duration(720).ease(d3.easeQuad).style('opacity', 1); // Main data line

