import React, { Component } from 'react';
import './App.css';
import * as d3 from 'd3'

const constants = {
  linear: [
    306.06644452,
    0.00430901514
  ],
  quadratic: [
    314.574751,
    0.00210065413,
    0.0000000973625567
  ],
  cosine: [
    314.569048,
    0.00210632696,
    0.0000000970576557,
    2.86111474,
    -0.554076698]
}

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      originalData: [],
      styles: {
        dark: '#333344',
        normal: '#555566',
        light: '#999999',
        line: '#EEEEEE',
        theme: '#4BA824'
      },
      chartState: 'initial'
    }
  }
  componentDidMount() {
    let margin = {top: 20, right: 60, bottom: 40, left: 0},
      width = 800,
      height = 600,
      innerWidth = width - margin.left - margin.right,
      innerHeight = height - margin.top - margin.bottom,
      svg = d3.select('#App')
      .append('svg')
        .attr('width', width)
        .attr('height', height)

    d3.csv('data/weekly_in_situ_co2_mlo.csv').then(data => {
      data.forEach(function(d) {
        d.date = d3.timeParse('%Y-%m-%d')(d.date)
        d.level = +d.level
      })
      this.setState({
        originalData: data
      }, () => {
        let x = d3.scaleTime()
          .domain(d3.extent(data, (d, i) => {return d.date}))
          .range([0, innerWidth])
          .nice(),
          y = d3.scaleLinear()
          .domain(d3.extent(data, (d, i) => {return d.level}))
          .range([innerHeight, 0])
          .nice(),
          totalLength = null

        // Grid lines
        svg.append('g')
          .attr('class', 'y grid')
          .attr('transform', 'translate(' + margin.left + ', ' + height + ')')
          .call(d3.axisBottom(x)
              .ticks(5)
              .tickSize(-height)
              .tickFormat('')
          )
        svg.append('g')
          .attr('class', 'x grid')
          .attr('transform', 'translate(' + margin.left + ', ' + margin.top + ')')
          .call(d3.axisLeft(y)
              .ticks(5)
              .tickSize(-width)
              .tickFormat('')
          )

        // Axes
        svg.append('g')
          .attr('class', 'x axis')
          .attr('transform', 'translate(' + margin.left + ', ' + (innerHeight + margin.top) + ')')
          .call(d3.axisBottom(x)
            .ticks(5)
            .tickSize(0)
            .tickPadding(24))
        svg.append('g')
          .attr('class', 'y axis')
          .attr('transform', 'translate(' + (innerWidth + margin.left) + ', ' + margin.top + ')')
          .call(d3.axisRight(y)
            .ticks(5)
            .tickSize(0)
            .tickPadding(32))
        svg.append('line')
          .attr('class', 'bottom-axis')
          .attr("x1", 0)
          .attr("y1", height - margin.bottom)
          .attr("x2", width)
          .attr("y2", height - margin.bottom)

        // Main data line
        let dataLine = svg.append('path')
          .datum(data)
          .attr('class', 'data-line')
          .attr('d', d3.line()
            .x(function(d) { return x(d.date) + margin.left })
            .y(function(d) { return y(d.level) + margin.top })
          )

        // Animate data line
        totalLength = dataLine.node().getTotalLength()
        dataLine.attr("stroke-dasharray", totalLength + " " + totalLength)
          .attr("stroke-dashoffset", totalLength)
          .transition()
            .delay(400)
            .duration(600)
            .ease(d3.easeCubicOut)
            .attr('stroke-dashoffset', 0)

        let updateReg = function(newState) {
          let regLineLength = null,
            minDate = d3.min(data, function(d) {return d.date}),
            xDays = data.map(d => Math.ceil((d.date.getTime() - minDate.getTime())/(1000*60*60*24))),
            yReg = null
          switch(newState) {
            case 'linear':
              yReg = xDays.map(d => constants.linear[0] + constants.linear[1]*d)
              let regLine = svg.append('path')
                .datum(data)
                .attr('class', 'reg-line')
                .attr('d', d3.line()
                  .x(function(d) { return x(d.date) + margin.left })
                  .y(function(d, i) {return y(yReg[i]) + margin.top })
              ),
              regLineLabel = svg.append('g')
                .attr('class', 'reg-line-label')
                .attr('transform',
                  'translate('
                  + (x(data[Math.ceil(data.length/3*2)]['date']) + margin.left + 20)
                  + ', '
                  + (y(yReg[Math.ceil(yReg.length/3*2)]) + margin.top + 20)
                  + ')'
                )
              regLineLabel.style('opacity', 0)
                .transition()
                  .duration(600)
                  .ease(d3.easeQuadOut)
                  .style('opacity', 1)
              regLineLabel.append('rect')
                .attr('class', 'reg-line-label-rect linear')
              let regLineLabelText = regLineLabel.append('text')
                  .attr('class', 'reg-line-label-text')
              regLineLabelText.append('tspan')
                  .attr('class', 'linear span')
                  .text('y = αx')
              regLineLength = regLine.node().getTotalLength()
              regLine.attr("stroke-dasharray", regLineLength + " " + regLineLength)
                .attr("stroke-dashoffset", regLineLength)
                .transition()
                  .duration(600)
                  .ease(d3.easeCubicOut)
                  .attr('stroke-dashoffset', 0)
              dataLine.node().style.stroke = 'var(--line)'
              d3.selectAll('.reg-line-label-text>tspan:not(.linear)')
                .each(function(d) {
                  let currentSpan = d3.select(this)
                  if (!currentSpan.node().classList.contains('off')) {
                    currentSpan.node().classList.add('off')
                  }
                })
              break
            case 'quadratic':
              yReg = xDays.map(d => constants.quadratic[0] + constants.quadratic[1]*d + constants.quadratic[2]*d**2)
              d3.select('.reg-line').transition()
                .duration(600)
                .ease(d3.easeQuad)
                .attr('d', d3.line()
                  .x(function(d) { return x(d.date) + margin.left })
                  .y(function(d, i) {return y(yReg[i]) + margin.top })
                )
              regLineLength = d3.select('.reg-line').node().getTotalLength()
              d3.select('.reg-line').attr("stroke-dasharray", 0)
                .attr('stroke-dashoffset', 0)
              d3.select('.reg-line-label-rect')
                .attr('class', 'reg-line-label-rect quadratic')
              d3.select('.reg-line-label-text').append('tspan')
                .attr('class', 'quadratic span')
                .text(' + βx\u00b2')
                .attr('fill-opacity', 0)
                  .transition()
                    .duration(600)
                    .ease(d3.easeQuadOut)
                    .attr('fill-opacity', 1)
              d3.selectAll('.reg-line-label-text>tspan:not(.quadratic)')
                .each(function(d) {
                  let currentSpan = d3.select(this)
                  if (!currentSpan.node().classList.contains('off')) {
                    currentSpan.node().classList.add('off')
                  }
                })
              break
            case 'cosine':
              yReg = xDays.map(d => constants.cosine[0]
                + constants.cosine[1]*d
                + constants.cosine[2]*d**2
                + constants.cosine[3]*Math.cos(2*Math.PI*d/365.25 + constants.cosine[4])
              )
              d3.select('.reg-line').attr("stroke-dasharray", regLineLength + " " + regLineLength)
                .transition()
                .duration(600)
                .ease(d3.easeQuad)
                .attr('d', d3.line()
                  .x(function(d) { return x(d.date) + margin.left })
                  .y(function(d, i) {return y(yReg[i]) + margin.top })
              )
              regLineLength = d3.select('.reg-line').node().getTotalLength()
              d3.select('.reg-line').attr("stroke-dasharray", 0)
                .attr('stroke-dashoffset', 0)
              d3.select('.reg-line-label-rect')
                .attr('class', 'reg-line-label-rect cosine')
              d3.select('.reg-line-label-text').append('tspan')
                .attr('class', 'cosine span')
                .text(' + cos(2πt + φ)')
              d3.selectAll('.reg-line-label-text>tspan:not(.cosine)')
                .each(function(d) {
                  let currentSpan = d3.select(this)
                  if (!currentSpan.node().classList.contains('off')) {
                    currentSpan.node().classList.add('off')
                  }
                })
              break
            default:
          }
        }
        this.setState({
          updateReg: updateReg
        })
      })
    })

    window.addEventListener('click', () => {
      if (this.state.chartState === 'initial') {
        this.setState({
          chartState: 'linear'
        }, () => this.state.updateReg('linear'))
      } else if (this.state.chartState === 'linear') {
        this.setState({
          chartState: 'quadratic'
        }, () => this.state.updateReg('quadratic'))
      } else if (this.state.chartState === 'quadratic') {
        this.setState({
          chartState: 'cosine'
        }, () => this.state.updateReg('cosine'))
      }
    })
  }
  render() {
    let styles = this.state.styles
    return (
      <div
        id="App"
        style={{
            '--dark': styles.dark,
            '--normal': styles.normal,
            '--light': styles.light,
            '--line': styles.line,
            '--theme': styles.theme,
            '--theme-readable': styles.themeReadable
          }}>
      </div>
    )
  }
}

export default App;
