import React, { Component } from 'react';
import './App.css';
import * as d3 from 'd3'

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      originalData: []
    }
    this.parse = this.parse.bind(this)
  }
  parse(data) {
    let x = d3.scaleLinear()
      .domain(d3.extent(data, (d, i) => {return d.date}))
      .range([0, 400]),
      y = d3.scaleLinear()
      .domain(d3.extent(data, (d, i) => {return d.level}))
      .range([400, 0])

    let xAxis = d3.axisBottom(x)
      .ticks(4)

    let svg = d3.select('body')
      .append('svg')
        .attr('width', 300)
        .attr('height', 150)

    svg.append('g')
      .attr('class', 'x axis')
      .call(xAxis);
  }
  componentDidMount() {
    d3.csv("data/weekly_in_situ_co2_mlo.csv").then(data => {
      data.forEach(function(d) {
        d.date = new Date(d.date)
        d.level = +d.level
      })
      this.setState({
        originalData: data
      }, () => this.parse(this.state.originalData))
    })
  }
  render() {
    return (
      <div className="App">
      </div>
    )
  }
}

export default App;
