import React, { Component } from 'react';
import './App.css';
import * as d3 from 'd3'

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      originalData: []
    }
  }
  componentDidMount() {
    let width = 460,
      height = 400,
      svg = d3.select('body')
      .append('svg')
        .attr('width', width)
        .attr('height', height)

    d3.csv("data/weekly_in_situ_co2_mlo.csv").then(data => {
      data.forEach(function(d) {
        d.date = d3.timeParse("%Y-%m-%d")(d.date)
        d.level = +d.value
      })
      // console.log(data[0]['date'])
      this.setState({
        originalData: data
      }, () => {
        let x = d3.scaleLinear()
          .domain(d3.extent(data, (d, i) => {return d.date}))
          .range([0, 400]),
          y = d3.scaleLinear()
          .domain(d3.extent(data, (d, i) => {return d.level}))
          .range([400, 0])

        svg.append('g')
          .attr('class', 'x axis')
          .attr('transform', 'translateY(' + height + 'px)')
          .call(d3.axisBottom(x).ticks(5))
        svg.append('g')
          .attr('class', 'y axis')
          .call(d3.axisLeft(y).ticks(5))
        svg.append("path")
          .datum(data)
          .attr("fill", "none")
          .attr("stroke", "steelblue")
          .attr("stroke-width", 1.5)
          .attr("d", d3.line()
            .x(function(d) { return x(d.date) })
            .y(function(d) { return y(d.level) })
          )
      })
    })
  }
  render() {
    return (
      <div className="App">
      </div>
    )
  }
}

export default App;
