import React, { Component } from 'react';
import './Section.css';

import LineChart from './viz/LineChart';

class Section extends Component {
  constructor(props) {
    super(props)
    this.renderSwitch = this.renderSwitch.bind(this)
  }
  renderSwitch() {
    switch (this.props.content.key) {
      case 'line-chart':
        return <LineChart data={this.props.data} content={this.props.content.vizContent} />
    }
  }
  componentDidMount() {
    let vizObserver = new IntersectionObserver(
      (entries, observer) => {
        if (entries[0].isIntersecting && !this.state.lineChartCreated) {
          this.setState({
            vizCreated: [true]
          })
        }
      },
      {
        threshold: [0.6, 1]
      })
    lineChartObserver.observe(document.querySelector('#line-chart'))
  }
  render() {
    return (
      <div className="section-wrap line-chart">
        <div className="limit-box">
          <h2 className="header limited">
              <span className="header-span animate right">A rapid</span>
              <span className="header-span animate left">increase.</span>
          </h2>
          <p
            className="para limited animate blur"
            style={{
              '--animation-speed': '800ms',
              '--animation-delay': '800ms'
          }}>
            A simple line plot can show us how the CO<sub>2</sub> levels (measured in ppm - parts per million) has changed since the first recordings in 1980.
          </p>
        </div>
        {this.renderSwitch()}
      </div>
    )
  }
}

export default Section;
