import React, { Component } from 'react';
import './App.css';
import * as d3 from 'd3'

const styles = {
  background: '#FFFFFF',
  invertedBackground: '#060608',
  dark: '#121214',
  invertedDark: '#FFFFFF',
  normal: '#444455',
  light: '#888899',
  line: '#E8E8E9',
  theme: '#4ba824'
},
vizs = {
  lineChart: [
    {
      state: 'initial',
      des: 'The original data looks like this.',
      params: null
    },
    {
      state: 'linear',
      des: 'There is a clear and consistent upward trend through the years.',
      params: [
        306.06644452,
        0.00430901514
      ]
    },
    {
      state: 'quadratic',
      des: 'But a quadratic trend line would be a better fit.',
      params: [
        314.574751,
        0.00210065413,
        0.0000000973625567
      ]
    },
    {
      state: 'cosine',
      des: 'The levels also have a seasonal component. This could be modeled by adding a cosine term to the function.',
      params: [
        314.569048,
        0.00210632696,
        0.0000000970576557,
        2.86111474,
        -0.554076698]
    }
  ]
}

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      originalData: [],
      lineChartCreated: false
    }
    this.createLineChart = this.createLineChart.bind(this)
  }
  componentDidMount() {
    d3.csv('data/weekly_in_situ_co2_mlo.csv').then(data => {
      data.forEach(function(d) {
        d.date = d3.timeParse('%Y-%m-%d')(d.date)
        d.level = +d.level
      })
      this.setState({
        originalData: data
      }, () => {
        let animationObserver = new IntersectionObserver(
          (entries, observer) => {
            entries.forEach((entry) => {
              if (entry.isIntersecting) {
                entry.target.classList.add('on')
              }
            })
          },
          {
            threshold: 1
          })
        document.querySelectorAll('.animate').forEach(el => {
          animationObserver.observe(el)
        })

        let lineChartObserver = new IntersectionObserver(
          (entries, observer) => {
            if (entries[0].isIntersecting) {
              if (entries[0].intersectionRatio === 1 && this.state.lineChartCreated) {
                // this.state.updateLineChart('initial')
              } else if (!this.state.lineChartCreated) {
                this.createLineChart(this.state.originalData)
                this.setState({lineChartCreated: true})
              }
            }
          },
          {
            threshold: [0.6, 1]
          })
        lineChartObserver.observe(document.querySelector('#line-chart'))

        let lineChartScrollObserver = new IntersectionObserver(
          (entries, observer) => {
            entries.forEach((entry) => {
              if (entry.isIntersecting) {
                let index = entry.target.dataset.index,
                  el = document.querySelector('#line-chart .viz-des-text:nth-child(' + (Number(index) + 1) + ')')
                el.classList.add('on')
                el.classList.remove('off')
                document.querySelectorAll('#line-chart .viz-des-text:not(:nth-child(' + (Number(index) + 1) + '))').forEach(el => {
                  if (el.classList.contains('on')) {
                    el.classList.add('off')
                    setTimeout(() => {
                      el.classList.remove('on')
                    }, 800)
                  }
                })
                this.state.updateLineChart(vizs.lineChart[index].state)
              }
            })
          },
          {
            rootMargin: '0 0 -25%',
            threshold: 1
          })
          document.querySelectorAll('.viz-scroll-anchor').forEach(el => {
            lineChartScrollObserver.observe(el)
          })
      })
    })
  }
  render() {
    return (
      <div
        id="App"
        style={{
            '--background': styles.background,
            '--inverted-background': styles.invertedBackground,
            '--dark': styles.dark,
            '--inverted-dark': styles.invertedDark,
            '--normal': styles.normal,
            '--light': styles.light,
            '--line': styles.line,
            '--theme': styles.theme,
            '--theme-readable': styles.themeReadable
          }}>
          <div className="hero-wrap">
            <h1 className="hero-text">
              <span className="hero-span span-1">C</span>
              <span className="hero-span span-2">O</span>
              <sub className="hero-span span-3">2</sub>
            </h1>
            <p className="hero-des">
              An exploration into new formats for data visualization, with CO<sub>2</sub> measurements from the Mauna Loa Observatory in Hawaii.
            </p>
          </div>
          <div className="section-wrap line-chart">
            <div className="limit-box">
              <h2 className="header limited">
                  <span className="header-span animate right">A rapid</span>
                  <span className="header-span animate left">increase.</span>
              </h2>
              <p
                className="para limited animate blur"
                style={{
                  '--animation-speed': '800ms',
                  '--animation-delay': '800ms'
              }}>
                A simple line plot can show us how the CO<sub>2</sub> levels (measured in ppm - parts per million) has changed since the first recordings in 1980.
              </p>
            </div>
            <div className="viz-outer-wrap">
              <div className="viz-wrap" id="line-chart">
                <div className="viz-des-wrap">
                  {vizs.lineChart.map((des, index) => {
                    return (
                      <p className="viz-des-text" key={index}>{des.des}</p>
                    )
                  })}
                </div>
              </div>
              <div className="viz-scroll-box">
                {vizs.lineChart.map((des, index) => {
                  return (
                    <div className="viz-scroll-anchor" data-index={index} key={index}></div>
                  )
                })}
              </div>
            </div>
          </div>
      </div>
    )
  }
}

export default App;
