import React, { Component } from 'react';
import './App.css';
import * as d3 from 'd3'

const constants = {
  linear: [
    560,
    -0.64
  ],
  quadratic: [
    560,
    -0.44,
    -0.0004
  ]
}

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      originalData: [],
      styles: {
        dark: '#333344',
        light: '#999999',
        line: '#F1F1F1',
        theme: '#F99806',
        themeReadable: '#F99806'
      },
      chartState: 'initial'
    }
  }
  componentDidMount() {
    let margin = {top: 20, right: 60, bottom: 40, left: 0},
      width = 800,
      height = 600,
      innerWidth = width - margin.left - margin.right,
      innerHeight = height - margin.top - margin.bottom,
      svg = d3.select('#App')
      .append('svg')
        .attr('width', width)
        .attr('height', height)

    d3.csv('data/weekly_in_situ_co2_mlo.csv').then(data => {
      data.forEach(function(d) {
        d.date = d3.timeParse('%Y-%m-%d')(d.date)
        d.level = +d.level
      })
      this.setState({
        originalData: data
      }, () => {
        let x = d3.scaleTime()
          .domain(d3.extent(data, (d, i) => {return d.date}))
          .range([0, innerWidth])
          .nice(),
          y = d3.scaleLinear()
          .domain(d3.extent(data, (d, i) => {return d.level}))
          .range([innerHeight, 0])
          .nice(),
          totalLength = null

        // Grid lines
        svg.append('g')
          .attr('class', 'y grid')
          .attr('transform', 'translate(' + margin.left + ', ' + height + ')')
          .call(d3.axisBottom(x)
              .ticks(5)
              .tickSize(-height)
              .tickFormat('')
          )
        svg.append('g')
          .attr('class', 'x grid')
          .attr('transform', 'translate(' + margin.left + ', ' + margin.top + ')')
          .call(d3.axisLeft(y)
              .ticks(5)
              .tickSize(-width)
              .tickFormat('')
          )

        // Axes
        svg.append('g')
          .attr('class', 'x axis')
          .attr('transform', 'translate(' + margin.left + ', ' + (innerHeight + margin.top) + ')')
          .call(d3.axisBottom(x).ticks(5).tickSize(0).tickPadding(24))
        svg.append('g')
          .attr('class', 'y axis')
          .attr('transform', 'translate(' + (innerWidth + margin.left) + ', ' + margin.top + ')')
          .call(d3.axisRight(y).ticks(5).tickSize(0).tickPadding(32))
        svg.append('line')
          .attr('class', 'bottom-axis')
          .attr("x1", 0)
          .attr("y1", height - margin.bottom)
          .attr("x2", width)
          .attr("y2", height - margin.bottom)

        // Main data line
        let dataLine = svg.append('path')
          .datum(data)
          .attr('class', 'data-line')
          .attr('d', d3.line()
            .x(function(d) { return x(d.date) + margin.left })
            .y(function(d) { return y(d.level) + margin.top })
          )

        // Animate data line
        totalLength = dataLine.node().getTotalLength()
        dataLine.attr("stroke-dasharray", totalLength + " " + totalLength)
          .attr("stroke-dashoffset", totalLength)
          .transition()
            .delay(400)
            .duration(600)
            .ease(d3.easeCubicOut)
            .attr('stroke-dashoffset', 0)

        let updateChart = function(newState) {
          switch(newState) {
            case 'linear':
              let regLineLength = null
              var regLine = svg.append('path')
                .datum(data)
                .attr('class', 'reg-line')
                .attr('d', d3.line()
                  .x(function(d) { return x(d.date) + margin.left })
                  .y(function(d) { return constants.linear[0]
                    + constants.linear[1]*x(d.date)
                    + margin.top })
              )
              regLineLength = regLine.node().getTotalLength()
              regLine.attr("stroke-dasharray", regLineLength + " " + regLineLength)
                .attr("stroke-dashoffset", regLineLength)
                .transition()
                  .duration(600)
                  .ease(d3.easeCubicOut)
                  .attr('stroke-dashoffset', 0)
              dataLine.node().style.stroke = 'var(--line)'
              break
            case 'quadratic':
              d3.select('.reg-line').transition()
                .duration(600)
                .ease(d3.easeQuad)
                .attr('d', d3.line()
                  .x(function(d) { return x(d.date) + margin.left })
                  .y(function(d) { return constants.quadratic[0]
                    + constants.quadratic[1]*x(d.date)
                    + constants.quadratic[2]*x(d.date)**2
                    + margin.top })
              )
              break
          }
        }

        svg.node().addEventListener('click', () => {
          if (this.state.chartState === 'initial') {
            this.setState({
              chartState: 'linear'
            }, () => updateChart('linear'))
          } else if (this.state.chartState === 'linear') {
            this.setState({
              chartState: 'quadratic'
            }, () => updateChart('quadratic'))
          }
        })
      })
    })
  }
  render() {
    let styles = this.state.styles
    return (
      <div
        id="App"
        style={{
            '--dark': styles.dark,
            '--light': styles.light,
            '--line': styles.line,
            '--theme': styles.theme,
            '--theme-readable': styles.themeReadable
          }}>
      </div>
    )
  }
}

export default App;
