import React, { Component } from 'react';
import './App.css';
import * as d3 from 'd3'

const constants = {
  linear: [
    306.06644452,
    0.00430901514
  ],
  quadratic: [
    314.574751,
    0.00210065413,
    0.0000000973625567
  ],
  cosine: [
    314.569048,
    0.00210632696,
    0.0000000970576557,
    2.86111474,
    -0.554076698]
}

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      originalData: [],
      styles: {
        background: '#FFFFFF',
        invertedBackground: '#060608',
        dark: '#121214',
        invertedDark: '#FFFFFF',
        normal: '#444455',
        light: '#888899',
        line: '#E8E8E9',
        theme: '#4ba824'
      },
      chartState: 'initial'
    }
    this.createLineChart = this.createLineChart.bind(this)
    this.updateLineChart = this.updateLineChart.bind(this)
  }
  bindObservers() {

  }
  createLineChart(data) {
    let margin = {top: 20, right: 60, bottom: 40, left: 0},
      width = window.innerWidth - 540,
      height = window.innerHeight - 200,
      innerWidth = width - margin.left - margin.right,
      innerHeight = height - margin.top - margin.bottom,
      svg = d3.select('#line-chart')
      .append('svg')
        .attr('width', width)
        .attr('height', height),
      x = d3.scaleTime()
      .domain(d3.extent(data, (d, i) => {return d.date}))
      .range([0, innerWidth])
      .nice(),
      y = d3.scaleLinear()
      .domain(d3.extent(data, (d, i) => {return d.level}))
      .range([innerHeight, 0])
      .nice(),
      totalLength = null

    // Grid lines
    svg.append('g')
      .attr('class', 'y grid')
      .attr('transform', 'translate(' + margin.left + ', ' + height + ')')
      .call(d3.axisBottom(x)
          .ticks(5)
          .tickSize(-height)
          .tickFormat('')
      )
    d3.selectAll('.y.grid>.tick').each(function(d, i) {
      d3.select(this).attr("stroke-dasharray", height + " " + height)
        .attr("stroke-dashoffset", height)
        .transition()
          .duration(600)
          .ease(d3.easeQuad)
          .attr('stroke-dashoffset', function() {
            if (i%2 === 0) {
              return 0
            } else {
              return 2*height
            }
          })
    })
    svg.append('g')
      .attr('class', 'x grid')
      .attr('transform', 'translate(' + margin.left + ', ' + margin.top + ')')
      .call(d3.axisLeft(y)
          .ticks(5)
          .tickSize(-width)
          .tickFormat('')
      )
    d3.selectAll('.x.grid>.tick').each(function(d, i) {
      d3.select(this).attr("stroke-dasharray", width + " " + width)
        .attr("stroke-dashoffset", width)
        .transition()
          .duration(600)
          .ease(d3.easeQuad)
          .attr('stroke-dashoffset', function() {
            if (i%2 === 0) {
              return 0
            } else {
              return 2*width
            }
          })
    })

    // Axes
    svg.append('g')
      .attr('class', 'x axis')
      .attr('transform', 'translate(' + margin.left + ', ' + (innerHeight + margin.top) + ')')
      .style("opacity", 0)
      .transition()
        .duration(720)
        .ease(d3.easeQuad)
        .style('opacity', 1)
      .call(d3.axisBottom(x)
        .ticks(5)
        .tickSize(0)
        .tickPadding(24))
    svg.append('g')
      .attr('class', 'y axis')
      .attr('transform', 'translate(' + (innerWidth + margin.left) + ', ' + margin.top + ')')
      .style("opacity", 0)
      .transition()
        .duration(720)
        .ease(d3.easeQuad)
        .style('opacity', 1)
      .call(d3.axisRight(y)
        .ticks(5)
        .tickSize(0)
        .tickPadding(32))
    svg.append('line')
      .attr('class', 'bottom-axis')
      .attr("x1", 0)
      .attr("y1", height - margin.bottom)
      .attr("x2", width)
      .attr("y2", height - margin.bottom)
      .style("opacity", 0)
      .transition()
        .duration(720)
        .ease(d3.easeQuad)
        .style('opacity', 1)

    // Main data line
    let dataLine = svg.append('path')
      .datum(data)
      .attr('class', 'data-line')
      .attr('d', d3.line()
        .x(function(d) { return x(d.date) + margin.left })
        .y(function(d) { return y(d.level) + margin.top })
      )

    // Animate data line
    totalLength = dataLine.node().getTotalLength()
    dataLine.attr("stroke-dasharray", totalLength + " " + totalLength)
      .attr("stroke-dashoffset", totalLength)
      .transition()
        .delay(600)
        .duration(600)
        .ease(d3.easeCubicOut)
        .attr('stroke-dashoffset', 0)

    let updateReg = function(newState) {
      let regLineLength = null,
        minDate = d3.min(data, function(d) {return d.date}),
        xDays = data.map(d => Math.ceil((d.date.getTime() - minDate.getTime())/(1000*60*60*24))),
        yReg = null
      switch(newState) {
        case 'linear':
          yReg = xDays.map(d => constants.linear[0] + constants.linear[1]*d)
          let regLine = svg.append('path')
            .datum(data)
            .attr('class', 'reg-line')
            .attr('d', d3.line()
              .x(function(d) { return x(d.date) + margin.left })
              .y(function(d, i) {return y(yReg[i]) + margin.top })
          ),
          regLineLabel = svg.append('g')
            .attr('class', 'reg-line-label')
            .attr('transform',
              'translate('
              + (x(data[Math.ceil(data.length*0.52)]['date']) + margin.left + 60)
              + ', '
              + (y(yReg[Math.ceil(yReg.length*0.52)]) + margin.top + 20)
              + ')'
            )
          regLineLabel.style('opacity', 0)
            .transition()
              .duration(720)
              .ease(d3.easeQuad)
              .style('opacity', 1)
          regLineLabel.append('rect')
            .attr('class', 'reg-line-label-rect linear')
          let regLineLabelText = regLineLabel.append('text')
              .attr('class', 'reg-line-label-text')
          regLineLabelText.append('tspan')
              .attr('class', 'linear span')
              .text('y = αx')
          regLineLength = regLine.node().getTotalLength()
          regLine.attr("stroke-dasharray", regLineLength + " " + regLineLength)
            .attr("stroke-dashoffset", regLineLength)
            .transition()
              .duration(600)
              .ease(d3.easeQuadOut)
              .attr('stroke-dashoffset', 0)
          dataLine.node().style.stroke = 'var(--line)'
          d3.selectAll('.reg-line-label-text>tspan:not(.linear)')
            .each(function(d) {
              let currentSpan = d3.select(this)
              if (!currentSpan.node().classList.contains('off')) {
                currentSpan.node().classList.add('off')
              }
            })
          break
        case 'quadratic':
          yReg = xDays.map(d => constants.quadratic[0] + constants.quadratic[1]*d + constants.quadratic[2]*d**2)
          d3.select('.reg-line').transition()
            .duration(600)
            .ease(d3.easeQuadOut)
            .attr('d', d3.line()
              .x(function(d) { return x(d.date) + margin.left })
              .y(function(d, i) {return y(yReg[i]) + margin.top })
            )
          regLineLength = d3.select('.reg-line').node().getTotalLength()
          d3.select('.reg-line').attr("stroke-dasharray", 0)
            .attr('stroke-dashoffset', 0)
          d3.select('.reg-line-label-rect')
            .attr('class', 'reg-line-label-rect quadratic')
          d3.select('.reg-line-label-text').append('tspan')
            .attr('class', 'quadratic span')
            .text(' + βx\u00b2')
            .style('opacity', 0)
              .transition()
                .duration(720)
                .ease(d3.easeQuad)
                .style('opacity', 1)
          d3.selectAll('.reg-line-label-text>tspan:not(.quadratic)')
            .each(function(d) {
              let currentSpan = d3.select(this)
              if (!currentSpan.node().classList.contains('off')) {
                currentSpan.node().classList.add('off')
              }
            })
          break
        case 'cosine':
          yReg = xDays.map(d => constants.cosine[0]
            + constants.cosine[1]*d
            + constants.cosine[2]*d**2
            + constants.cosine[3]*Math.cos(2*Math.PI*d/365.25 + constants.cosine[4])
          )
          d3.select('.reg-line').attr("stroke-dasharray", regLineLength + " " + regLineLength)
            .transition()
              .duration(600)
              .ease(d3.easeCubicOut)
              .attr('d', d3.line()
                .x(function(d) { return x(d.date) + margin.left })
                .y(function(d, i) {return y(yReg[i]) + margin.top })
            )
          regLineLength = d3.select('.reg-line').node().getTotalLength()
          d3.select('.reg-line').attr("stroke-dasharray", 0)
            .attr('stroke-dashoffset', 0)
          d3.select('.reg-line-label-rect')
            .attr('class', 'reg-line-label-rect cosine')
          d3.select('.reg-line-label-text').append('tspan')
            .attr('class', 'cosine span')
            .text(' + cos(2πt + φ)')
            .style('opacity', 0)
              .transition()
                .duration(720)
                .ease(d3.easeQuad)
                .style('opacity', 1)
          d3.selectAll('.reg-line-label-text>tspan:not(.cosine)')
            .each(function(d) {
              let currentSpan = d3.select(this)
              if (!currentSpan.node().classList.contains('off')) {
                currentSpan.node().classList.add('off')
              }
            })
          break
        default:
      }
    }
    this.setState({
      updateReg: updateReg
    })
    window.addEventListener("resize", () => {
      svg.attr("width", window.innerWidth - 540)
        .attr("height", window.innerHeight - 200);
    });
  }
  updateLineChart() {
    if (this.state.chartState === 'initial') {
      this.setState({
        chartState: 'linear'
      }, () => this.state.updateReg('linear'))
    } else if (this.state.chartState === 'linear') {
      this.setState({
        chartState: 'quadratic'
      }, () => this.state.updateReg('quadratic'))
    } else if (this.state.chartState === 'quadratic') {
      this.setState({
        chartState: 'cosine'
      }, () => this.state.updateReg('cosine'))
    }
  }
  componentDidMount() {
    d3.csv('data/weekly_in_situ_co2_mlo.csv').then(data => {
      data.forEach(function(d) {
        d.date = d3.timeParse('%Y-%m-%d')(d.date)
        d.level = +d.level
      })
      this.setState({
        originalData: data
      }, () => {
        // window.addEventListener('click', this.updateLineChart())
        animationObserver = new IntersectionObserver(
          (entries, observer) => {
            entries.forEach((entry) => {
              if (entry.isIntersecting) {
                entry.target.classList.add('on')
              }
            })
          },
          {
            threshold: 0.3
          })
        document.querySelectorAll('.animate').forEach(el => {
          animationObserver.observe(el)
        })

        lineChartObserver = new IntersectionObserver(
          (entries) => {
            if (entries[0].isIntersecting) {
              if (entries[0].intersectionRatio >= 0.95)
              this.createLineChart(this.state.originalData)
            }
          },
          {
            threshold: [0.6, 1]
          })
        lineChartObserver.observe(document.querySelector('#line-chart'))
      })
    })
  }
  render() {
    let styles = this.state.styles
    return (
      <div
        id="App"
        style={{
            '--background': styles.background,
            '--inverted-background': styles.invertedBackground,
            '--dark': styles.dark,
            '--inverted-dark': styles.invertedDark,
            '--normal': styles.normal,
            '--light': styles.light,
            '--line': styles.line,
            '--theme': styles.theme,
            '--theme-readable': styles.themeReadable
          }}>
          <div className="hero-wrap">
            <h1 className="hero-text">
              <span className="hero-span span-1">C</span>
              <span className="hero-span span-2">O</span>
              <sub className="hero-span span-3">2</sub>
            </h1>
            <p className="hero-des">
              An exploration into new formats for data visualization, with CO<sub>2</sub> measurements from the Mauna Loa Observatory in Hawaii.
            </p>
          </div>
          <div className="section-wrap line-chart">
            <div className="limit-box">
              <h2 className="header limited">
                  <span className="header-span animate right">A rapid</span>
                  <span className="header-span animate left">increase.</span>
              </h2>
              <p className="para limited animate blur">
                A simple line plot can show us how the CO<sub>2</sub> levels (measured in ppm - parts per million) has changed since the first recordings in 1980.
              </p>
            </div>
            <div className="viz" id="line-chart">
            </div>
          </div>
      </div>
    )
  }
}

export default App;
