import React, { Component } from 'react';
import './App.css';
import * as d3 from 'd3'

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      originalData: [],
      styles: {
        dark: '#333344',
        light: '#999999',
        line: '#F1F1F1',
        theme: '#F99806',
        themeReadable: '#F99806'
      }
    }
  }
  componentDidMount() {
    let margin = {top: 10, right: 30, bottom: 30, left: 30},
      width = 460,
      height = 400,
      innerWidth = width - margin.left - margin.right,
      innerHeight = height - margin.top - margin.bottom,
      svg = d3.select('body')
      .append('svg')
        .attr('width', width)
        .attr('height', height)

    d3.csv('data/weekly_in_situ_co2_mlo.csv').then(data => {
      data.forEach(function(d) {
        d.date = d3.timeParse('%Y-%m-%d')(d.date)
        d.level = +d.level
      })
      this.setState({
        originalData: data
      }, () => {
        let x = d3.scaleTime()
          .domain(d3.extent(data, (d, i) => {return d.date}))
          .range([0, innerWidth])
          .nice(),
          y = d3.scaleLinear()
          .domain(d3.extent(data, (d, i) => {return d.level}))
          .range([innerHeight, 0])
          .nice()

        svg.append('g')
          .attr('class', 'x axis')
          .attr('transform', 'translate(' + margin.left + ', ' + (innerHeight + margin.top) + ')')
          .call(d3.axisBottom(x).ticks(5).tickSize(0).tickPadding(10))
        svg.append('g')
          .attr('class', 'y axis')
          .attr('transform', 'translate(' + margin.left + ', ' + margin.top + ')')
          .call(d3.axisLeft(y).ticks(5).tickSize(0).tickPadding(10))

        svg.append('g')
          .attr('class', 'x grid')
          .attr('stroke', this.state.styles.line)
          .attr('transform', 'translate(' + margin.left + ', ' + margin.top + ')')
          .call(d3.axisLeft(y)
              .ticks(5)
              .tickSize(-width)
              .tickFormat('')
          )
        svg.append('g')
          .attr('class', 'y grid')
          .attr('transform', 'translate(' + margin.left + ', ' + (innerHeight + margin.top) + ')')
          .call(d3.axisBottom(x)
              .ticks(5)
              .tickSize(-height)
              .tickFormat('')
          )

        svg.append('path')
          .datum(data)
          .attr('fill', 'none')
          .attr('stroke', this.state.styles.dark)
          .attr('stroke-width', 1.5)
          .attr('d', d3.line()
            .x(function(d) { return x(d.date) + margin.left })
            .y(function(d) { return y(d.level) + margin.top })
          )
      })
    })
  }
  render() {
    return (
      <div className="App">
      </div>
    )
  }
}

export default App;
