import React, { Component } from 'react';
import './App.css';
import * as d3 from 'd3'

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      originalData: []
    }
  }
  componentDidMount() {
    let margin = {top: 10, right: 30, bottom: 30, left: 60},
      width = 460 - margin.left - margin.right,
      height = 400 - margin.top - margin.bottom,
      svg = d3.select('body')
      .append('svg')
        .attr('width', 300)
        .attr('height', 150)
    d3.csv("data/weekly_in_situ_co2_mlo.csv").then(data => {
      data.forEach(function(d) {
        d.date = d3.timeParse("%Y-%m-%d")(d.date)
        d.level = +d.value
      })
      console.log(typeof data[0]['level'])
      this.setState({
        originalData: data
      }, () => {
        let x = d3.scaleLinear()
          .domain(d3.extent(data, (d, i) => {return d.date}))
          .range([0, 400]),
          y = d3.scaleLinear()
          .domain(d3.extent(data, (d, i) => {return d.level}))
          .range([400, 0])

        svg.append('g')
          .attr('class', 'x axis')
          .attr("transform", "translate(0," + height + ")")
          .call(d3.axisBottom(x).ticks(10))
          .append('g')
          .attr('class', 'y axis')
          .call(d3.axisLeft(y).ticks(10));
      })
    })
  }
  render() {
    return (
      <div className="App">
      </div>
    )
  }
}

export default App;
